// Função para remover acentos e deixar minúsculo
const normalize = (str) => {
  if (!str) return "";
  return str
    .toString()
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "") // Remove acentos
    .trim();
};

// Pega o termo de busca e normaliza
const rawSearchTerm = $item("0").$node["Loop Over Items"].json["Nome da Música"];
const searchTerm = normalize(rawSearchTerm);

// Obtém o valor fixo do campo "Artista"
const artistaFixo = $item(0).$node["Loop Over Items"].json["Artista"];

const results = items
  .filter(item => {
    const trackName = normalize(item.json.name);
    const albumName = normalize(item.json.album?.name);
    return trackName.includes(searchTerm) || albumName.includes(searchTerm);
  })
  .map(item => {
    const trackArtist = item.json.artists?.[0]?.name;
    const albumArtist = item.json.album?.artists?.[0]?.name;
    const finalArtist = trackArtist || albumArtist || "Desconhecido";

    return {
      json: {
        "Artista": artistaFixo,
        "Nome da Música": item.json.name,
        "Quem Gravou": finalArtist,
        "Link da Música": item.json.external_urls?.spotify || "Não disponível",
        "Plataforma": "Spotify"
      }
    };
  });

return results.length > 0
  ? results
  : [{ json: { aviso: "Nenhum dado retornado", "Artista": artistaFixo } }];

// Extrai vídeos da página 1 e prepara página 2
const response = $input.first().json;
const items = response.items || [];
const nextPageToken = response.nextPageToken || '';
const loopData = $item("0").$node["Loop Over Items"].json;
const query = loopData["Nome da Música"];
const artista = loopData["Artista"];

// Formata vídeos da página 1
const videosPagina1 = items.map(item => ({
  titulo: item.snippet?.title || 'Sem título',
  canal: item.snippet?.channelTitle || 'Canal desconhecido',
  videoId: item.id?.videoId
}));

return [{
  json: {
    query: query,
    artista: artista,
    pageToken: nextPageToken,
    pagina: 2,
    videosPagina1: videosPagina1
  }
}];

// Combina resultados das duas páginas do YouTube
const response = $input.first().json;
const pagina1Data = $('Processar YouTube Página 1').first().json;
const items = response.items || [];
const artista = pagina1Data.artista;
const nomeMusica = pagina1Data.query;

// Formata vídeos da página 2
const videosPagina2 = items.map(item => ({
  titulo: item.snippet?.title || 'Sem título',
  canal: item.snippet?.channelTitle || 'Canal desconhecido',
  videoId: item.id?.videoId
}));

// Combina todas as páginas
const todosVideos = [...pagina1Data.videosPagina1, ...videosPagina2];

// Retorna cada vídeo formatado para a planilha
const resultados = todosVideos.map(v => ({
  json: {
    "Artista": artista,
    "Nome da Música": v.titulo,
    "Quem Gravou": v.canal,
    "Link da Música": `https://www.youtube.com/watch?v=${v.videoId}`,
    "Plataforma": "YouTube"
  }
}));

return resultados.length > 0 ? resultados : [{ json: { aviso: "Nenhum vídeo encontrado no YouTube", "Artista": artista } }];

